import time


class Solution(object):

    # scan from Left to Right, then reverse
    # slower than next one, time O(N), space O(1)
    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        ret = 0
        # Scan From Left to Right
        opened = closed = 0
        for i in range(0, len(s)):
            if s[i] == '(':
                opened += 1
            else:
                closed += 1
            if closed == opened:
                ret = max(ret, closed*2)
            # break Valid Condition, re-positioning
            if closed > opened:
                closed = opened = 0

        # Scan From Right to Left
        closed = opened = 0
        for i in range(-1, len(s)*(-1)-1, -1):
            if s[i] == '(':
                opened += 1
            else:
                closed += 1
            if closed == opened:
                ret = max(ret, closed*2)
            # break Valid Condition, re-positioning
            if closed < opened:
                closed = opened = 0

        return ret

    # faster than previous one, but consuming more memory space: time O(N), space O(N)
    def longestValidParentheses_useStack(self, s):
        """
        :type s: str
        :rtype: int
        """
        ret = 0
        idxList = [-1]
        for i in range(0, len(s)):
            if s[i] == '(':
                idxList.append(i)
            else:
                #idxList.pop()
                #Tips: Del is more effecient than pop, when u don't need the popped item
                del idxList[-1]
                if not idxList:
                    idxList.append(i)
                else:
                    ret = max(ret, i - idxList[-1])
        return ret

    # Dynamic Programming, slower than stack
    def longestValidParentheses_useDP(self, s):
        """
        :type s: str
        :rtype: int
        """
        s = "*" + s
        ret = 0
        dp = [0] #longest valid parentheses ending at this index
        for i in range(1, len(s)):
            if s[i] == '(':
                dp.append(0)
            else:
                if s[i-1] == '(':
                    cur = dp[-2] + 2
                    ret = max(ret, cur)
                    dp.append(cur)
                elif s[i-1] == ')':
                    if s[i-dp[i-1]-1] == '(':
                        cur = dp[-1] + 2 + dp[i-dp[-1]-2]
                        ret = max(ret, cur)
                        dp.append(cur)
                    else:
                        dp.append(0)
                else:
                    dp.append(0)
        #print dp
        return ret





