    def longestValidParentheses_exceedTimeLimit(self, s):
        total = len(s)

        while total > 0 and s[-1] == "(":
            s = s[:-1]
            total -= 1
        while total > 0 and s[0] == ")":
            s = s[1:]
            total -= 1

        ret = 0
        start = 0
        status = []
        opened = 0
        closed = 0
        triggerFlag = False
        for i in range(0, total):
            if s[i] == '(':
                opened += 1
                closed += 0
            else:
                opened += 0
                closed += 1
            status.append([opened, closed])
            if opened < closed:
                triggerFlag = True
            if triggerFlag == False and s[i] != "(" and opened == closed:
                if ret < opened * 2:
                    ret = opened * 2

        while start < total:
            # travel
            if s[start] == '(':
                opened = -1
                closed = 0
            else:
                opened = 0
                closed = -1
            breakFlag = False
            for i in range(start + 1, total):
                status[i][0] += opened
                status[i][1] += closed
                if status[i][0] < status[i][1]:
                    breakFlag = True
                if breakFlag == False and s[start + 1] == '(' and s[i] != "(" and status[i][0] == status[i][1]:
                    if ret < status[i][0] * 2:
                        ret = status[i][0] * 2

            start += 1

        return ret

